import { ElementRef } from '@angular/core';
import { Validators } from '@angular/forms';

successMessage: ElementRef;

export class UserLoginComponent {
  constructor(formBuilder, router, userService) {
    this.formBuilder = formBuilder;
    this.router = router;
    this.userService = userService;
    this.failure = {
      value: false
    };
    this.userData = null;
    /* --login form--- */

    this.loginForm = this.formBuilder.group({
      userId: [null, [Validators.required]],
      password: [null, [Validators.required, Validators.minLength(8)]]
    });
  }

  ngOnInit() {
    this.failure.value = false;
  }
  /* -----method for login------- */


  submit() {
    this.userService.userLogin(this.loginForm.value).subscribe(data => {
      this.userData = data;
      this.failure.value = false;
      console.log(this.userData.userId);
      localStorage.setItem("userId", this.userData.userId);
      this.router.navigate(["/userHome"]);
      this.showSuccessMessage();
    }, error => {
      this.failure.value = true;
      this.loginForm.reset();
    });
  } // Method to display the success message


  showSuccessMessage() {
    this.successMessage.nativeElement.classList.add('show');
    setTimeout(() => {
      this.successMessage.nativeElement.classList.remove('show');
    }, 5000); // Hide the message after 5 seconds (adjust as needed)
  }

}
